import React,{useState} from 'react';
import axios from 'axios';
import {useNavigate,Link} from 'react-router-dom'

const AddPirate = (props) => {
  const {pirates, setPirates} = props;
    const [pirateName, setPirateName] = useState("")
    const [pirateImg, setPirateImg] = useState("")
    const [pirateCatch, setPirateCatch] = useState("")
    const [crewPos, setCrewPos] = useState("")
    const [treasureNum, setTreasureNum] = useState("")
    const [pegLeg, setPegLeg] = useState(true)
    const [hookHand, setHookHand] = useState(true)
    const [eyePatch, setEyePatch] = useState(true)
    const [errors, setErrors] = useState({})
    const navigate = useNavigate()



    const submitHandler=(e)=>{
      e.preventDefault();
          axios.post('http://localhost:8000/api/pirates',{
          pirateImg,
          pirateCatch,
          crewPos,
          treasureNum,
          pegLeg,
          eyePatch,
          hookHand
          })
          .then((res)=>{
              console.log(res);
              console.log(res.data);
              setPirateName("");
              setPirateImg("");
              setPirateCatch("");
              setCrewPos("");
              setTreasureNum("");
              setPegLeg("");
              setEyePatch("");
              setHookHand("");

              setPirates([...pirates,res.data]);
              navigate('/pirates');
          })
          .catch((err)=>{
              console.log(err.response.data.error.errors)
              setErrors(err.response.data.error.errors)
          })
          }  

  const nameHandler=(e)=>{
    setErrors("")
    setPirateName(e.target.value)
  }
  const imgHandler=(e)=>{
      setErrors("")
      setPirateImg(e.target.value)
  }
  const catchHandler=(e)=>{
      setErrors("")
      setPirateCatch(e.target.value)
  }
  const crewHandler=(e)=>{
      setErrors("")
      setCrewPos(e.target.value)
  }
  const treasureHandler=(e)=>{
      setErrors("")
      setTreasureNum(e.target.value)
  }
  const pegLegHandler=(e)=>{
      setErrors("")
      setPegLeg(e.target.value)
  } 
  const hookHandHandler=(e)=>{
    setErrors("")
    setHookHand(e.target.value)
}   
const eyePatchHandler=(e)=>{
    setErrors("")
    setEyePatch(e.target.value)
}      

  return (
    <div>
        <div className="container">
            <div className="">
                <h1>Add Pirate</h1>
                <button className="btn" onClick={()=>navigate("/pirates") }>Crew Board</button> 
            </div>
            <div className="container">
                <form onSubmit={submitHandler} className="formInputDiv">
                <div className="formdiv">
                        <div className="formInputDiv">
                            <label className="input-label">Pirate Name:<br/>
                                {errors.petName ? <p>{errors.petName.message}</p>:null}
                                <input className="inputbox" type="text" label="Name" onChange={nameHandler} value={petName} />
                            </label>
                            <label className="input-label">mage URL:<br/>
                                {errors.petType ? <p>{errors.petType.message}</p>:null}
                                <input className="inputbox" type="text" label="Type" onChange={typeHandler} value={petType} />
                            </label>
                            <label className="input-label">Pet Description:<br/>
                                {errors.description ? <p>{errors.description.message}</p>:null}
                                <input className="inputbox" type="text" label="Type" onChange={descHandler} value={description} />
                            </label>
                        </div> 
                        <p className="input-label"> Skills (optional):</p>
                        <div className="formInputDiv">
                                <label className="input-label">Skill 1:<br/>
                                    {errors.skill1 ? <p>{errors.skill1.message}</p>:null}
                                    <input className="inputbox" type="text" label="Type" onChange={skill1Handler} value={skill1} />
                                </label>
                                <label className="input-label">Skill 2:<br/>
                                    {errors.skill2 ? <p>{errors.skill2.message}</p>:null}
                                    <input className="inputbox" type="text" label="Type" onChange={skill2Handler} value={skill2} />
                                </label>
                                <label className="input-label">Skill 3:<br/>
                                    {errors.skill3 ? <p>{errors.skill3.message}</p>:null}
                                    <input className="inputbox" type="text" label="Type" onChange={skill3Handler} value={skill3} />
                                </label>
                        </div>
                    </div>    
                    <br/>
                    <div className="btndiv">
                        <button onClick={submitHandler} type="submit" className = "btn hover">Add Pet</button>
                    </div>
                </form>
            </div>
        </div>
    </div>
)
}

export default AddPirate
