import {useState,useEffect} from 'react'
import axios from 'axios'
import {useNavigate} from 'react-router-dom'
import {Link} from 'react-router-dom'
import {useParams} from 'react-router-dom'



const AllPirates = () => {

  const [pirates,setPirates] = useState([])
    const [errors, setErrors] = useState({})
    const navigate = useNavigate()
    const {id} = useParams();

    useEffect(()=>{
      axios.get('http://localhost:8000/api/pirates')
      .then((res)=>{
          setPets(res.data.pirates)
      })
      .catch((err)=>{
          console.log(err.response.data.error.errors)
          setErrors(err.response.data.error.errors)
      })
  },[pirates])  

  return (
    <div className="container">
        <div className="">
            <h1>Pirate Crew</h1>
            <button className="btn hover" onClick={()=>navigate("/pirates/new") }>Add Pirate</button> 
        </div>
        <div className="tableDiv">
              {
                  pirates.map((item,idx)=>(  
                      <div className="card" key={idx}>
                          <p>{item.pirateName}</p>
                          <p>{item.pirateImg}</p>
                                  <div>
                                      <button className="btn" onClick={()=>navigate(`/pets/${item._id}`) }>Details</button>
                                      <button className="btn" onClick={()=>navigate(`/pets/edit/${item._id}`)}>Edit</button>
                                  </div>

                      </div>
                              ))
              } 
        </div> 
    </div>
  )
}

export default AllPirates
