      setErrors("")
      setTreasureNum(e.target.value)
  }
  const pegLegHandler=(e)=>{
      setErrors("")
      setPegLeg(e.target.value)
  } 
  const hookHandHandler=(e)=>{
    setErrors("")
    setHookHand(e.target.value)
}   
const eyePatchHandler=(e)=>{
    setErrors("")
    setEyePatch(e.target.value)
}      

  return (
    <div>AddPirate</div>
