import './App.css';
import {BrowserRouter, Routes, Route, Navigate} from "react-router-dom"
import {useState} from 'react'
import AllPirates from './components/AllPirates'
import AddPirate from './components/AddPirate';
import EditPirate from './components/EditPirate';
import OnePirate from './components/OnePirate';


function App() {
  const [pirates,setPirates]= useState([])
  return (
    <div className="App">
      <BrowserRouter>
      <Routes>
        <Route element = {<Navigate to="/pets" />} path="/" />
        <Route element={< AllPirates pets={pirates} setPets={setPirates} />} path="/pirates" />
        <Route element={< AddPirate pets={pirates} setPets={setPirates}/>} path="/pirates/new"/>
        <Route element={< EditPirate pets={pirates} setPets={setPirates}/>} path="/pets/edit/:id"/>
        <Route element={< OnePirate pets={pirates} setPets={setPirates} />} path="/pets/:id" />        
      </Routes>
      </BrowserRouter>

    </div>
  );
}

export default App;
